# Code
```
int mod=1e9+7;
class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int n=board.size(),m=board[0].length();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(m, vector<int>(2, 0)));
        dp[n-1][m-1][1]=1;
        for(int i=m-2;i>=0;i--){
            if(board[n-1][i]=='X' || dp[n-1][i+1][0]==-1) dp[n-1][i][0]=-1;
            else{
                dp[n-1][i][0]=dp[n-1][i+1][0]+board[n-1][i]-'0';
                dp[n-1][i][1]=1;
            }
        }
        for(int i=n-2;i>=0;i--){
            if(board[i][m-1]=='X' || dp[i+1][m-1][0]==-1) dp[i][m-1][0]=-1;
            else{
                dp[i][m-1][0]=dp[i+1][m-1][0]+board[i][m-1]-'0';
                dp[i][m-1][1]=1;
            }
        }
        for(int i=n-2;i>=0;i--){
            for(int j=m-2;j>=0;j--){
                if( board[i][j]=='X' ) dp[i][j][0]=-1;
                else if(board[i][j]=='E') {
                    int mx=max(dp[i+1][j][0], max(dp[i+1][j+1][0],dp[i][j+1][0]));
                    if(mx==-1) dp[i][j][0]=-1;
                    else{
                        dp[i][j][0]=mx;
                        if(dp[i+1][j][0]==mx) dp[i][j][1]=(dp[i][j][1]+dp[i+1][j][1])%mod;
                        if(dp[i+1][j+1][0]==mx) dp[i][j][1]=(dp[i][j][1]+dp[i+1][j+1][1])%mod;
                        if(dp[i][j+1][0]==mx) dp[i][j][1]=(dp[i][j][1]+dp[i][j+1][1])%mod;
                    } 
                    return {max(mx,0), dp[0][0][1]};
                }
                else{
                    int mx=max(dp[i+1][j][0], max(dp[i+1][j+1][0],dp[i][j+1][0]));
                    if(mx==-1) dp[i][j][0]=-1;
                    else{
                        dp[i][j][0]=board[i][j]-'0'+mx;
                        if(dp[i+1][j][0]==mx) dp[i][j][1]=(dp[i][j][1]+dp[i+1][j][1])%mod;
                        if(dp[i+1][j+1][0]==mx) dp[i][j][1]=(dp[i][j][1]+dp[i+1][j+1][1])%mod;
                        if(dp[i][j+1][0]==mx) dp[i][j][1]=(dp[i][j][1]+dp[i][j+1][1])%mod;
                    } 
                }
            }
        }
        return {};

    }
};
