class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& b) {
        int mx = 0, n = b.size();
        int M = 1000000007;
        vector<vector<pair<int,int>>> dp(n,vector<pair<int,int>>(n,{0,0}));
        dp[n-1][n-1] = {0,1};
        for(int i=n-2;i>=0;--i){
            // last col can only take from just below
            if(b[i][n-1] != 'X') dp[i][n-1] = {(b[i][n-1]-'0')+dp[i+1][n-1].first,1};
            else break;
        }
        for(int i=n-2;i>=0;--i){
            // last row can only take from just below
            if(b[n-1][i] != 'X') dp[n-1][i] = {(b[n-1][i]-'0')+dp[n-1][i+1].first,1}; 
            else break;
        }
        // No for the middle part
        for(int i=n-2;i>=0;--i){
            for(int j=n-2;j>=0;--j){
                if(b[i][j] != 'X'){
                    int ri = dp[i][j+1].first, dn = dp[i+1][j].first, dr = dp[i+1][j+1].first;
                    int mx = max({ri,dn,dr});
                    if(ri == mx && dp[i][j+1].second > 0) dp[i][j].second = (1ll*dp[i][j].second + dp[i][j+1].second)%M;
                    if(dr == mx && dp[i+1][j+1].second > 0) dp[i][j].second = (1ll*dp[i][j].second + dp[i+1][j+1].second)%M; 
                    if(dn == mx && dp[i+1][j].second > 0) dp[i][j].second = (1ll*dp[i][j].second + dp[i+1][j].second)%M;
                    dp[i][j].first = (dp[i][j].first + (b[i][j]-'0')+mx)%M;
                }
            }
        }
        if(dp[0][0].second == 0) return {0,0};
        return {dp[0][0].first-'E'+'0', dp[0][0].second};
    }
};


