            pii pre = {0, 0};
            for (int j = n - 1; j >= 0; j--) {
                pii temp = dp[j];
                if (i == m - 1 and j == n - 1)  dp[j].second = 1;

                if (grid[i][j] == 'X') {
                    dp[j].first = dp[j].second = 0;
                } else {
                    int val = grid[i][j] - '0', dist = 0;
                    int curMax = val + max({dp[j].first, dp[j + 1].first, pre.first});
                    if (val + dp[j].first == curMax) dist = (dist + dp[j].second) % mod;
                    if (val + dp[j + 1].first == curMax) dist = (dist + dp[j + 1].second) % mod;
                    if (val + pre.first == curMax) dist = (dist + pre.second) % mod;
                    dp[j] = {curMax, dist};
                }
                pre = temp;
            }
        }
        if (dp[0].second == 0) dp[0].first = 0;
        return vector<int>({dp[0].first, dp[0].second});
    }
};
