from functools import cache
mod = 1000000007
class Solution:
    def pathsWithMaxScore(self, board: List[str]) -> List[int]:
        @cache
        def DP(i,j):
            if board[i][j]=="X":
                return [0,0]
            if i==0 and j==0:
                return [0,1]

            if i==0:
                previous = DP(0, j-1)
                return [previous[0]+int(board[i][j]), previous[1]]
            if j==0:
                previous = DP(i-1, 0)
                return [previous[0]+int(board[i][j]), previous[1]]

            left = DP(i-1,j)
            up   = DP(i,j-1)
            upleft=DP(i-1,j-1)

            val = max(left[0], up[0], upleft[0])
            count = 0
            if left[0]==val:
                count +=left[1]
            if up[0]==val:
                count +=up[1]   
            if upleft[0]==val:
                count +=upleft[1]   

            if i==len(board)-1 and j==len(board)-1 :
                if count ==0:
                    val =0
                return [val, count%mod] 
            return [val+int(board[i][j]), count%mod] 

        return DP( len(board)-1,len(board)-1 )



