class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {

        int n = board.size();
        vector<pair<int,int>> dp(n, {0,0});
        const int MOD = 1000000007;
        dp[0] = {0,1};

        for(int j = 1; j < n; j++)
        {
            if(board[0][j] != 'X')
                dp[j] = { dp[j - 1].first + board[0][j] - '0', 1};
        }

        for(int i = 1; i < n; i++)
        {
            vector<pair<int,int>> _dp(n, {0,0});
            if(board[i][0] != 'X' && dp[0].second != 0)
                _dp[0] = {dp[0].first + board[i][0] - '0', dp[0].second};

            for(int j = 1; j < n; j++)
            {   
                if(board[i][j] == 'X')
                    continue;
                int maxSum = max( {_dp[j - 1].first, dp[j - 1].first, dp[j].first} );
                long long int ways = 0;
                ways += maxSum == _dp[j - 1].first ? _dp[j - 1].second : 0;
                ways += maxSum == dp[j - 1].first ? dp[j - 1].second : 0;
                ways += maxSum == dp[j].first ? dp[j].second : 0;
                if(ways == 0)
                    continue;
                int cellValue = board[i][j] == 'S' ? 0 : (board[i][j] - '0');
                _dp[j] = {maxSum + cellValue, ways % MOD};
            }
            dp.swap(_dp);
        }

        return {dp[n - 1].first, dp[n - 1].second};
    }
};
