class Solution {
public:
    typedef pair<int, int> pr;

    vector<int> pathsWithMaxScore(vector<string>& board) {
        ios::sync_with_stdio(false);
        cin.tie(0);
        cout.tie(0);

        char n = board.size();

        board[0][0] = '0';

        pr tb[n][n], p = {0, 0};
        int mod = 1e9 + 7;

        tb[n - 1][n - 1] = {1, 1};

        for(char i = n - 2; i != -1; --i) {
            tb[i][n - 1] = (board[i][n - 1] == 'X' || board[i + 1][n - 1] == 'X') ? p : pr{tb[i + 1][n - 1].first + board[i][n - 1] - '0', tb[i + 1][n - 1].second};
            tb[n - 1][i] = (board[n - 1][i] == 'X' || board[n - 1][i + 1] == 'X') ? p : pr{tb[n - 1][i + 1].first + board[n - 1][i] - '0', tb[n - 1][i + 1].second};
        }

        pr R, D, RD;
        int M, sum;
        for(char i = n - 2; i != -1; --i) {
            auto &b = board[i];
            auto &t0 = tb[i];
            auto &t1 = tb[i + 1];
            for(char j = n - 2; j != -1; --j) {
                if(b[j] == 'X')
                    t0[j] = p;
                else {
                    R = t0[j + 1];
                    D = t1[j];
                    RD = t1[j + 1];
                    M = max(max(R.first, D.first), RD.first);

                    sum = 0;
                    if(R.first == M)  sum = (sum + R.second) % mod;
                    if(D.first == M)  sum = (sum + D.second) % mod;
                    if(RD.first == M) sum = (sum + RD.second) % mod;

                    t0[j] = M ? pr{M + b[j] - '0', sum} : pr{0, 0};
                }
            }
        }

        return {max(0, tb[0][0].first - 1), tb[0][0].second};
    }
};
