class Solution:
    def pathsWithMaxScore(self, board: List[str]) -> List[int]:
        n = len(board)
        dp = [[-math.inf]*n for _ in range(n)]
        count = [[0]*n for _ in range(n)]

        dp[-1][-1] = 0
        count[-1][-1] = 1
        for i in range(n-1, -1, -1):
            for j in range(n-1, -1, -1):
                if i == (n-1) and j == (n-1):
                    continue

                if board[i][j] == 'X':
                    continue

                dp[i][j] = dp[i][j+1] if j+1 < n else -math.inf
                count[i][j] = count[i][j+1] if j+1 < n else 0

                tmp = dp[i+1][j] if i+1 < n else -math.inf
                if tmp > dp[i][j]:
                    dp[i][j] = tmp
                    count[i][j] = count[i+1][j] if i+1 < n else 0
                elif tmp == dp[i][j]:
                    count[i][j] += count[i+1][j] if i+1 < n else 0

                tmp = dp[i+1][j+1] if i+1 < n and j+1 < n else -math.inf
                if tmp > dp[i][j]:
                    dp[i][j] = tmp
                    count[i][j] = count[i+1][j+1] if i+1 < n and j+1 < n else 0
                elif tmp == dp[i][j]:
                     count[i][j] += count[i+1][j+1] if i+1 < n and j+1 < n else 0

                if i != 0 or j != 0:
                    dp[i][j] += int(board[i][j])

        if dp[0][0] == -math.inf:
            return [0, 0]
        else:
            return [dp[0][0], count[0][0]%(10**9+7)]

