```Java []
class Solution {
    public int[] pathsWithMaxScore(List<String> board) {
        int MOD = (int)(1e9 + 7);
        int n = board.size();
        int[][] sum = new int[n][n];
        int[][] cnt = new int[n][n];
        cnt[n-1][n-1] = 1;
        int[][] m = {{-1, 0}, {0, -1}, {-1, -1}};

        for(int i = n - 1; i >= 0; i--){
            for(int j = n - 1; j >= 0; j--){
                char val = board.get(i).charAt(j);
                if(val == 'X' || val == 'S'){
                    continue; // sum, cnt = 0
                }

                int v = val - '0';
                int maxSum = 0;
                int maxCnt = 0;
                for(int k = 0; k < 3; k++){
                    int r = i - m[k][0];
                    int c = j - m[k][1];
                    if(r >= n || c >= n)
                        continue;
                    if(sum[r][c] > maxSum){
                        maxSum = sum[r][c];
                        maxCnt = cnt[r][c];
                    }
                    else if(sum[r][c] == maxSum){
                        maxCnt += cnt[r][c];
                    }
                }
                if(val == 'E')
                    sum[i][j] = maxSum;
                else
                    sum[i][j] = v + maxSum;
                cnt[i][j] = maxCnt % MOD;
            }
        }

        int[] ans = new int[2];
        ans[0] = sum[0][0];
        ans[1] = cnt[0][0];
        if(cnt[0][0] == 0)
            ans[0] = 0;
        return ans;
    }
}
