                for(int k = 0; k < 3; k++){
                    int r = i - m[k][0];
                    int c = j - m[k][1];
                    if(r >= n || c >= n)
                        continue;
                    if(sum[r][c] > maxSum){
                        maxSum = sum[r][c];
                        maxCnt = cnt[r][c];
                    }
                    else if(sum[r][c] == maxSum){
                        maxCnt += cnt[r][c];
                    }
                }
                if(val == 'E')
                    sum[i][j] = maxSum;
                else
                    sum[i][j] = v + maxSum;
                cnt[i][j] = maxCnt % MOD;
            }
        }

        int[] ans = new int[2];
        ans[0] = sum[0][0];
        ans[1] = cnt[0][0];
        if(cnt[0][0] == 0)
            ans[0] = 0;
        return ans;
    }
}
