class Solution {
public:
    int mod=1e9+7;
    pair<int,int>dp[101][101];
    pair<int,int>helper(int i , int j, vector<string>&nums){
        if(i==nums.size()||j==nums[0].size()||nums[i][j]=='X') return {INT_MIN,0};
        if(i==nums.size()-1&&j==nums[0].size()-1) return {0,1};
        int x=0;
        pair<int,int>p={-1,-1};
        if(dp[i][j]!=p){
            return dp[i][j];
        }
        if(nums[i][j]=='E') x=0 ;
        else{x=nums[i][j]-'0';}
        auto p1=helper(i+1,j,nums);
        auto p2=helper(i,j+1,nums);
        auto p3=helper(i+1,j+1,nums);
        int maxi=x+max(p1.first,max(p2.first,p3.first));
        int count=0;
        if(maxi==x+p1.first) count+=p1.second;
        if(maxi==x+p2.first) count+=p2.second;
        if(maxi==x+p3.first) count+=p3.second;
        return dp[i][j]={maxi,count%mod};
    }
    vector<int> pathsWithMaxScore(vector<string>& nums) {
        pair<int,int>p1={-1,-1};
        for(int i=0;i<=100;i++){
            for(int j=0;j<=100;j++){
                dp[i][j]=p1;
            }
        }
        auto ans=helper(0,0,nums);
        if(ans.first<0) return {0,0};
        return {ans.first,ans.second};
    }
};
