class Solution {
public:
    static vector<int> pathsWithMaxScore(const vector<string>& board) {
        const int n = size(board);
        vector<pair<int, int>> dp(n), dp2(n);
        dp[0] = {0, 1};
        for (int i = 1; i < n; ++i)
            if ('X' == board[0][i])
                break;
            else
                dp[i] = {dp[i - 1].first + board[0][i] - '0', 1};
        for (int j = 1; j < n; ++j) {
            if ('X' != board[j][0] && dp[0].second)
                dp2[0] = {dp[0].first + board[j][0] - '0', 1};
            else
                dp2[0] = {0, 0};
            for (int i = 1; i < n; ++i) {
                auto& [dist, cnt] = dp2[i] = {0, 0};
                if ('X' == board[j][i])
                    continue;
               dist = max({dp2[i - 1].first, dp[i].first, dp[i - 1].first});
                if (dist == dp2[i - 1].first)
                    cnt = (cnt + dp2[i - 1].second) % 1000000007;;
                if (dist == dp[i].first)
                    cnt = (cnt + dp[i].second) % 1000000007;
                if (dist == dp[i - 1].first)
                    cnt = (cnt + dp[i - 1].second) % 1000000007;
                dist += board[j][i] - '0';
            }
            swap(dp, dp2);
        }
        if (dp.back().second)
            return {dp.back().first - 'S' + '0', dp.back().second};
        return {0, 0};
    }
};
