    def pathsWithMaxScore(self, board: List[str]) -> List[int]:
        n = len(board)
        firstCol = [[0, 1]] + [[0, 0] for _ in range(n - 1)]
        for i in range(1, n):
            if board[i][0] == 'X':
                break
            firstCol[i] = [firstCol[i - 1][0] + int(board[i][0]), 1]

        prevRow = [[0, 1]] + [[0, 0] for _ in range(n - 1)]
        for j in range(1, n):
            if board[0][j] == 'X':
                break
            prevRow[j] = [prevRow[j - 1][0] + int(board[0][j]), 1]

