class Solution {
public:
    vector<vector<int>> dp;
    vector<vector<vector<int>>> dp1;
    int fun(vector<string> &board,int i,int j,int &m){
        if(i>=m || j>=m || board[i][j]=='X')
            return INT_MIN;
        if(i==(m-1) && j==(m-1))
            return 0;
        if(dp[i][j]!=-1)
            return dp[i][j];
        int p=0,a,b,c;
        if(board[i][j]!='E')
            p=int(board[i][j]-'0');
        a=p+fun(board,i,j+1,m);
        b=p+fun(board,i+1,j,m);
        c=p+fun(board,i+1,j+1,m);
        a=max(a,b);
        return dp[i][j]=max(a,c);
    }
    int fun1(vector<string> &board,int i,int j,int sum,int &m){
        if(i==(m-1) && j==(m-1))
            return sum==0;
        //if(sum==0)
            //return 0;
        if(i>=m || j>=m || board[i][j]=='X')
            return 0;
        if(sum>dp[i][j])
            return 0;
        if(dp1[i][j][sum]!=-1)
            return dp1[i][j][sum];
        int p=0;
        if(board[i][j]!='E')
            p=int(board[i][j]-'0');
        long int ans;
        ans=fun1(board,i,j+1,max(0,sum-p),m)+fun1(board,i+1,j,max(0,sum-p),m)+fun1(board,i+1,j+1,max(0,sum-p),m);
        return dp1[i][j][sum]=ans%1000000007;
    }
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int m=board.size();
        dp.resize(m+1,vector<int>(m+1,-1));
        int a,b=0;
        a=fun(board,0,0,m);
        a=max(0,a);
       dp1.resize(m,vector<vector<int>>(m,vector<int>(a+1,-1)));
        b=fun1(board,0,0,a,m);
        vector<int> res;
        res.push_back(a);
        res.push_back(b);
        return res;
    }
};
