class Solution:
    # Solve by DP
    # dp[i][j] = [max_score, path_count]
    def pathsWithMaxScore(self, board: List[str]) -> List[int]:
        n, MOD = len(board), pow(10,9) + 7
        dp = [[[] for i in range(n)] for j in range(n)]
        dp[0][0], dp[n-1][n-1] = [0,0], [0,1]
        for i in range(n-2,-1,-1):
            if board[i][n-1] != "X" and len(dp[i+1][n-1]) == 2:
                dp[i][n-1] = [dp[i+1][n-1][0] + int(board[i][n-1]),1]
        for i in range(n-2,-1,-1):
            if board[n-1][i] != "X" and len(dp[n-1][i+1]) == 2:
                dp[n-1][i] = [dp[n-1][i+1][0] + int(board[n-1][i]),1]

        for i in range(n-2,-1,-1):
            for j in range(n-2,-1,-1):
                if board[i][j] != "X":
                    candidates = []
                    if i == 0 and j == 0:
                        cur_score = 0
                    else:
                        cur_score = int(board[i][j])
                    if len(dp[i+1][j]) == 2:
                        candidates.append(dp[i+1][j])
                    if len(dp[i+1][j+1]) == 2:
                        candidates.append(dp[i+1][j+1])
                    if len(dp[i][j+1]) == 2:
                        candidates.append(dp[i][j+1])
                    if candidates:
                        candidates.sort(key = lambda x:[-x[0]])
                        max_score = candidates[0][0]
                        dp[i][j] = [max_score + cur_score, 0]
                        for score, count in candidates:
                            if score == max_score:
                                dp[i][j][1] += count
                                dp[i][j][1] %= MOD
        return dp[0][0]
