class Solution {
public:
    int n;
    vector<vector<pair<int,int>>> dp;
    bool ok;
    const int M = 1e9 + 7;

    bool inRange(int i, int j){
        return i >= 0 && j >= 0;
    }

    pair<int,int> func(vector<string>& board, int i, int j){
        if(i == 0 && j == 0){
            ok = true;
            return {0,1};
        }

        if(dp[i][j] != make_pair(-1,-1)) return dp[i][j];

        int score1 = 0, score2 = 0, score3 = 0, max_score = 0, cnt = 0, cnt1 = 0, cnt2 = 0, cnt3 = 0;

        if(inRange(i-1,j) && (isdigit(board[i-1][j]) || (i-1 == 0 && j == 0))){
            int val = (i-1 == 0 && j == 0 ? 0 : board[i-1][j]-'0');
            pair<int,int> p = func(board, i-1, j);
            score1 = val + p.first;
            cnt1 += p.second;
        }

        if(inRange(i,j-1) && (isdigit(board[i][j-1]) || (i == 0 && j-1 == 0))){
            int val = (i == 0 && j-1 == 0 ? 0 : board[i][j-1]-'0');
            pair<int,int> p = func(board, i, j-1);
            score2 = val + p.first;
            cnt2 += p.second;
        }

        if(inRange(i-1,j-1) && (isdigit(board[i-1][j-1]) || (i-1 == 0 && j-1 == 0))){
            int val = (i-1 == 0 && j-1 == 0 ? 0 : board[i-1][j-1]-'0');
            pair<int,int> p = func(board, i-1, j-1);
            score3 = val + p.first;
            cnt3 += p.second;
        }

        max_score = max(score1, max(score2, score3));

        if(max_score == score1) cnt = (cnt + cnt1) % M;
        if(max_score == score2) cnt = (cnt + cnt2) % M;
        if(max_score == score3) cnt = (cnt + cnt3) % M;


        return dp[i][j] = {max_score,cnt};
    }

    vector<int> pathsWithMaxScore(vector<string>& board) {
        n = board.size();
        ok = false;
        dp.assign(n,vector<pair<int,int>>(n,{-1,-1}));
        pair<int,int> p = func(board, n-1, n-1);
        int max_score = p.first;
        int cnt = p.second;

        if(ok) return {max_score, cnt};
        return {0,0};
    }
};
