class Solution:
    def pathsWithMaxScore(self, board: List[str]) -> List[int]:

        @lru_cache(None)
        def maxScore(row, col):
            if row == 0 and col == 0:
                return 0, 1

            best = -float("inf")
            cur = int(board[row][col]) if board[row][col] != "S" else 0
            paths = 0

            if row != 0 and board[row-1][col] != "X":
                best, paths = maxScore(row-1, col)
            if col != 0 and board[row][col-1] != "X":
                tmpScore, tmpPaths = maxScore(row, col-1)
                if tmpScore > best:
                    best = tmpScore
                    paths = tmpPaths
                elif tmpScore == best:
                    paths += tmpPaths
            if row != 0 and col != 0 and board[row-1][col-1] != "X":
                tmpScore, tmpPaths = maxScore(row-1, col-1)
                if tmpScore > best:
                    best = tmpScore
                    paths = tmpPaths
                elif tmpScore == best:
                    paths += tmpPaths
            return best + cur, paths

        score, paths = maxScore(len(board)-1, len(board[0])-1)

        if score == -inf:
            return [0, 0]

        return score, paths % (10**9+7)
