class Solution {
public:
    int mod=1e9+7;
    class ds{
        public:
        int sum=0;
        int path=0;
        public:
        ds(int sum,int path)
        {
            this->sum=sum;
            this->path=path;
        }
    };
    ds rec(vector<string> &nums,int i,int j,vector<vector<ds>> &dp)
    {
        if(i<0 or j<0 or i>=nums.size() or j>=nums[i].size() or nums[i][j]=='X')
        {
            return ds(-1e9,0);
        }
        if(i==0 and j==0)
        {
            return ds(0,1);
        }
        if(dp[i][j].sum!=-1 or dp[i][j].path!=-1)
            return dp[i][j];
        auto l=rec(nums,i-1,j,dp);
        auto r=rec(nums,i,j-1,dp);
        auto u=rec(nums,i-1,j-1,dp);
        int ma=max(l.sum,max(r.sum,u.sum));
        int pa=0;
        if(ma==l.sum)
            pa=(pa+l.path)%mod;
        if(ma==r.sum)
            pa=(pa+r.path)%mod;
        if(ma==u.sum)
            pa=(pa+u.path)%mod;
        ma=(ma+(int)nums[i][j]-'0')%mod;
        return dp[i][j]=ds(ma,pa);
    }
    vector<int> pathsWithMaxScore(vector<string>& nums) {
        int n=nums.size();
        vector<vector<ds>> dp(n+1,vector<ds>(n+1,ds(-1,-1)));
        auto ans=rec(nums,n-2,n-1,dp);
        auto ans1=rec(nums,n-2,n-2,dp);
        auto ans2=rec(nums,n-1,n-2,dp);
        int ma=max(ans.sum,max(ans1.sum,ans2.sum))%mod;
        int pa=0;
        if(ma==ans.sum)
            pa=(pa+ans.path)%mod;
        if(ma==ans1.sum)
            pa=(pa+ans1.path)%mod;
        if(ma==ans2.sum)
            pa=(pa+ans2.path)%mod;
        if(ma<0)
        {
            ma=0;
            pa=0;
        }
        vector<int> an;
        an.push_back(ma);
        an.push_back(pa);
        return an;

    }
