class Solution {
public:
	int N = 1e9 + 7;
	pair<int,int> dp[101][101];
	vector<int> pathsWithMaxScore(vector<string>& board) {
		int n = board.size();
		for(int i=0;i<n;i++){
			for(int j=0;j<n;j++){
				dp[i][j] = {INT16_MIN,1};
			}
		}
		dp[n-1][n-1].first = 0;
		dp[0][0].first = 0;
		for(int i=n-1;i>=0;i--){
			for(int j=n-1;j>=0;j--){
				if(board[i][j]=='X') continue;
					if(i+1<n){
						if((dp[i+1][j].first+board[i][j]-'0')%N > dp[i][j].first){
							dp[i][j] = {(dp[i+1][j].first + board[i][j]-'0')%N,dp[i+1][j].second};
						}else if((dp[i+1][j].first + board[i][j]-'0')%N == dp[i][j].first){
							dp[i][j].second = (dp[i][j].second + dp[i+1][j].second)%N;
						}
					}
					if(j+1<n){
						if((dp[i][j+1].first+board[i][j]-'0')%N > dp[i][j].first){
							dp[i][j] = {(dp[i][j+1].first + board[i][j]-'0')%N,dp[i][j+1].second};
						}else if((dp[i][j+1].first + board[i][j]-'0')%N == dp[i][j].first){
							dp[i][j].second = (dp[i][j].second + dp[i][j+1].second)%N;
						}
					}
