class Solution {
    public int[] pathsWithMaxScore(List<String> board) {
        int[][] dp = new int[board.size()][board.get(0).length()];
        int[][] solution = new int[dp.length][dp[0].length];
        // initialize
        solution[0][0] = 1;
        for (int i = 1; i < board.get(0).length(); i++) {
            char c = board.get(0).charAt(i);
            if (c == 'X') {
               break;
            } 
            dp[0][i] = dp[0][i - 1] + (c - '0');
            solution[0][i] = 1;
        }
        for (int i = 1; i < board.size(); i++) {
            char c = board.get(i).charAt(0);
            if (c == 'X') {
                break;
            }
            dp[i][0] = dp[i - 1][0] + (c - '0');
            solution[i][0] = 1;
        }

        for (int i = 1; i < board.size(); i++) {
            for (int j = 1; j < board.get(0).length(); j++) {
                char c = board.get(i).charAt(j);
                if (c == 'X') {
                  continue;
                }
                int t = Math.max(Math.max(dp[i - 1][j], dp[i - 1][j - 1]), dp[i][j - 1]);
                dp[i][j] = t > 0 ? t + (c == 'S' ? 0 : (c - '0')) : 0;
                if (dp[i - 1][j] == t) {
                    solution[i][j] += solution[i - 1][j];
                }
                if (dp[i - 1][j - 1] == t) {
                    solution[i][j] += solution[i - 1][j - 1];
                }
                if (dp[i][j - 1] == t) {
                    solution[i][j] += solution[i][j - 1];
                }
                // I dont really know why
                solution[i][j] = solution[i][j] % 1000000007;
            }
        }
        int k = dp[dp.length - 1][dp[0].length - 1];
        int p = solution[dp.length - 1][dp[0].length - 1];
        return new int[]{k,p};
    }
}
