class Solution {
public:

    int dp[101][101];
    int dp1[101][101];
    long long mod=1e9+7;
    int sum(vector<string>&b,int i,int j){

        int n=b.size(),m=b[0].size();

        if(i<0 || j<0 || b[i][j]=='X')return INT_MIN;        

        if(i==0 && j==0)return 0;

        if(dp[i][j]!=-1)return dp[i][j];

        int v=b[i][j]-'0';
        int f=sum(b,i-1,j-1);
        int s=sum(b,i-1,j);
        int t=sum(b,i,j-1);

        return dp[i][j]=v+max({f,s,t});

    }


    int solve(vector<string>&b,int i,int j){

        int n=b.size(),m=b[0].size();

        if(i<0 || j<0)return 0;        

        if(i==0 && j==0)return 1;

        if(dp1[i][j]!=-1)return dp1[i][j];

        int f=INT_MIN,s=INT_MIN,t=INT_MIN;

        if(i-1>=0)f=dp[i-1][j];

        if(j-1>=0)s=dp[i][j-1];

        if(i-1>=0 && j-1>=0)t=dp[i-1][j-1];

        int mx=max({f,s,t});

        int c=0;

        if(mx==f){
            c=(c+solve(b,i-1,j))%mod;
        }

        if(mx==s)c=(c+solve(b,i,j-1))%mod;

        if(t==mx)c=(c+solve(b,i-1,j-1))%mod;

        return dp1[i][j]=c;

    }

    vector<int> pathsWithMaxScore(vector<string>& b) {

        memset(dp,-1,sizeof(dp));
        int n=b.size(),m=b[0].size();
        b[0][0]='0';
        b[n-1][m-1]='0';
        dp[0][0]=0;
        // dp[n-1][m-1]=0;
        int mx=sum(b,n-1,m-1);

        if(mx<0)return {0,0};
        memset(dp1,-1,sizeof(dp1));

        int c=solve(b,n-1,m-1);
        return {mx,c};

    }
};
