/**
 * @param {string[]} board
 * @return {number[]}
 */
var pathsWithMaxScore = function(board) {
    const height = board.length; // x
    const width = board[0].length; // y
    const modulo = 10 ** 9 + 7;

    const remainingValueMemoMap = new Map();
    const cumScoreMemoMap = new Map();

    function dfs(x, y, cumScore) {
        // look up subproblem solution if it exists
        const memoizedRemainingValueAndFrequency = remainingValueMemoMap.get(`${x}_${y}`);
        if (memoizedRemainingValueAndFrequency != null) {
            cumScore += memoizedRemainingValueAndFrequency[0];
            return [cumScore, memoizedRemainingValueAndFrequency[1]];
        }

        // if we've gotten here before with a better cumScore, we can stop traversing.
        const memoizedCumScore = cumScoreMemoMap.get(`${x}_${y}`);
        if (memoizedCumScore != null && memoizedCumScore > cumScore) {
            return [0, 0];
        }
        // set cumScore as new best
        cumScoreMemoMap.set(`${x}_${y}`, cumScore);

        const currentSpaceVal = board[x][y];

        if (currentSpaceVal === 'E') {
            // we've reached the end, nothing to add
            return [cumScore, 1];
        } else if (currentSpaceVal !== 'S') {
            cumScore += parseInt(currentSpaceVal)
        }

        // instantiate array with valid options
        const options = [[x-1, y], [x, y-1], [x-1, y-1]].filter(option => option[0] >= 0 && option[1] >= 0 && board[option[0]][option[1]] !== 'X');

        let result = [0, 0]; // track the maxScore and frequency

        // dfs the options
        for (const option of options) {
            let dfsResult = dfs(option[0], option[1], cumScore);

            if (dfsResult[0] > result[0]) {
                result = dfsResult; // replace max
            } else if (dfsResult[0] === result[0]) {
                result[1] += dfsResult[1]; // increment freq
            }
        }

        result[1] %= modulo; // take the modulo here

        // memoize upon return from recursion the "max remaining value" 
        const maxRemainingValueAndFrequency = [result[0] - cumScore + parseInt(currentSpaceVal), result[1]];

        if (result[1] !== 0) { // dead end
            remainingValueMemoMap.set(`${x}_${y}`, maxRemainingValueAndFrequency);    
        }

        return result;
    }

    return dfs(height - 1, width - 1, 0);
};
