	public class Solution {
        const int Mod = 1000_000_007;
		public int[] PathsWithMaxScore(IList<string> board) {
			int len = board.Count;

            Item[][] dp = new Item[len + 1][];

            for (int i = 0; i < dp.Length; i++)
            {
                dp[i] = new Item[len + 1];
            }

            dp[len - 1][len - 1] = new Item
            {
                Pass = true,
                Paths = 1
            };

            for (int i = len - 1; i >= 0; i--)
            {
                for (int j = len - 1; j >= 0; j--)
                {
                    var curr = board[i][j];

                    ref var item = ref dp[i][j];

                    if (curr == 'X' || curr == 'S')
                    {
                        continue;
                    }

                    var score = curr == 'E' ? 0 : curr - '0';

                    var right = dp[i][j + 1];
                    var bottom = dp[i + 1][j];
                    var rb = dp[i + 1][j + 1];

                    item.Pass = right.Pass || bottom.Pass || rb.Pass;

                    if (item.Pass)
                    {
                        int maxScore = Math.Max(right.Score, Math.Max(bottom.Score, rb.Score));

                        if (right.Pass && right.Score == maxScore)
                        {
                            item.Paths += right.Paths;
                            item.Paths %= Mod;
                        }

                        if (bottom.Pass && bottom.Score == maxScore)
                        {
                            item.Paths += bottom.Paths;
                            item.Paths %= Mod;
                        }

                        if (rb.Pass && rb.Score == maxScore)
                        {
                            item.Paths += rb.Paths;
                            item.Paths %= Mod;
                        }

                        item.Score = maxScore + score;
                    }

                }
            }

            var first = dp[0][0];

            return new[] { first.Score, first.Paths };

        }

        struct Item
        {
            public bool Pass { get; set; }

            public int Score { get; set; }

            public int Paths { get; set; }

            public override string ToString()
            {
                return Pass ? $"[{Score}, {Paths}]" : "X";
            }
        }

	}
