	vector<int> pathsWithMaxScore(const vector<string>& g) {// tried top down with pos opt, not fast. 1st try for bottom up (not fast, but should be intuitive). T: O(m*n), S: O(n)
        int m = g.size(), n = g[0].size(), M = 1e9+7;
        vector<vector<vector<int> > > dp(2, vector<vector<int> > (n, vector<int>(2)));
        vector<int> dir = {0,-1,-1,0};
        for(int i=0; i<m; ++i)
            for(int j=0; j<n; ++j){
                dp[i%2][j] = {0,0};
                if(!i&&!j) {dp[0][0] = {0,1}; continue;}
                for(int k=0; k<3; ++k){ // can be replaced by: for(const auto& [x, y] : vector<pair<int,int>>{{i-1,j}, {i,j-1}, {i-1,j-1}}){
                    int x = i+dir[k], y = j+dir[k+1];
                    if(x<0 || y<0 || g[x][y] == 'X') continue;
                    if(dp[i%2][j][0] < dp[x%2][y][0]) dp[i%2][j] = {dp[x%2][y][0], dp[x%2][y][1]};
                    else if(dp[i%2][j][0] == dp[x%2][y][0]) dp[i%2][j][1] = (dp[i%2][j][1]%M+dp[x%2][y][1])%M;
                }
                dp[i%2][j][0] += isdigit(g[i][j])*(g[i][j]-'0');
            }
        return !dp[(m-1)%2][n-1][1] ? vector<int>{0,0} : dp[(m-1)%2][n-1];
    }

