class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int arr[3][2]={{1,0},{1,1},{0,1}};
        board[0][0]='0';
        // board[board.size()-1][board[board.size()-1].size()-1]='0';

        vector<vector<pair<int,int>>>dp(board.size(),vector<pair<int,int>>(board[0].size()));
        int mod=1e9+7;
        for(int i=dp.size()-1;i>=0;i--){
            for(int j=dp[0].size()-1;j>=0;j--){
                if(i==dp.size()-1&&j==dp[0].size()-1)
                {
                    dp[dp.size()-1][dp[0].size()-1]=make_pair(0,1);
                }
                else if(board[i][j]=='X'){
                    continue;
                }
                else if(i==dp.size()-1){
                    if(board[i][j+1]=='X'){
                        continue;
                    }else{
                    dp[i][j]=make_pair((dp[i][j+1].first+board[i][j]-'0')%mod,1);
                    }
                }
                else if(j==dp[0].size()-1){
                                        if(board[i+1][j]=='X'){
                        continue;
                    }else{
                    dp[i][j]=make_pair((dp[i+1][j].first+board[i][j]-'0')%mod,1);}
                }else{
                    int m=0;
                    int cnt=0;
                    for(int l=0;l<3;l++){
                        int xdash=i+arr[l][0];
                        int ydash=j+arr[l][1];
                        if(board[xdash][ydash]!='X'){
                            m=max(m,dp[xdash][ydash].first);
                        }
                    }

                    for(int l=0;l<3;l++){
                        int xdash=i+arr[l][0];
                        int ydash=j+arr[l][1];
                        if(board[xdash][ydash]!='X'){
                            if(dp[xdash][ydash].first==m){

                                cnt=(cnt+dp[xdash][ydash].second)%mod;
                            }

                        }
                    }

                    dp[i][j]=make_pair((m+board[i][j]-'0')%mod,cnt);
                }
            }
        }
        vector<int> v1;
        if(dp[0][0].second==0){
            dp[0][0].first=0;
        }
        v1.push_back(dp[0][0].first);        v1.push_back(dp[0][0].second);

        return v1;
    }
};
