    vector<int> pathsWithMaxScore(vector<string>& board) {
        const int N = 1000000007;
        int m=board.size();
        int n=board[0].size();
        vector<vector<pair<int,int>>>dp(m,vector<pair<int,int>>(n,{0,0}));
        dp[0][0]={0,1};
        for(int i=0;i<n;i++)
        {
            if(board[0][i]=='E')
            dp[0][i]={0,1};
            else if(board[0][i]=='X')
                dp[0][i]={-1,0};
            else
            {
                if(dp[0][i-1].first==-1)
                    dp[0][i]={-1,0};
                else
            dp[0][i] = {(board[0][i]-'0'+ dp[0][i-1].first),1};
            }
        }
        for(int i=0;i<m;i++)
        {
            if(board[i][0]=='E')
                continue;
            if(board[i][0]=='X')
                dp[i][0]={-1,0};
            else
            {
                if(dp[i-1][0].first==-1)
                    dp[i][0]={-1,0};
                else
                {
    dp[i][0]= {(dp[i-1][0].first + board[i][0]-'0'),1};
                }
            }            
        }
        for(int i=1;i<m;i++)
        {
            for(int j=1;j<n;j++)
            {
                if(board[i][j]=='X')
                    dp[i][j]={-1,0};
                else {
                        long long count=0;
    int maxi = max(dp[i][j-1].first,max(dp[i-1][j].first,dp[i-1][j-1].first));
                        if(dp[i][j-1].first==maxi)
                            count+=dp[i][j-1].second%N;
                        if(dp[i-1][j].first==maxi)
                            count+=dp[i-1][j].second%N;
                        if(dp[i-1][j-1].first==maxi)
                            count+=dp[i-1][j-1].second%N;
                        if(board[i][j]=='S')
                            dp[i][j] = {maxi,count%N};
                        else
                        dp[i][j]= {maxi+(board[i][j]-'0'),count%N};
                    }
