class Solution {
    int[][] dirs = {{0, -1}, {-1, 0}, {-1, -1}};
    long mod = 1000000007;
    Map<String, int[]> memo;
    int m, n;
    public int[] pathsWithMaxScore(List<String> board) {
        memo = new HashMap<>();
        m = board.size();
        n = board.get(0).length();
        char[][] b = new char[m][n];
        for(int i=0; i<m; i++)
            b[i] = board.get(i).toCharArray();

        int[] res = dfs(b, m-1, n-1);
        return res[0] < 0 ? new int[]{0, 0}: res;
    }
    public int[] dfs(char[][] board, int x, int y){
        if(x < 0 || y < 0 || board[x][y] == 'X')
            return new int[]{Integer.MIN_VALUE, 0};
        if(x == 0 && y == 0){
            return new int[]{0, 1};
        }
        if(memo.containsKey(x+"/"+y))
            return memo.get(x+"/"+y);
        int curValue = board[x][y] == 'S'? 0 : board[x][y]-'0';

        int res = Integer.MIN_VALUE, count = 0;
        for(int[] dir : dirs){
            int nx = x+dir[0], ny = y+dir[1];
            int[] tmp = dfs(board, nx, ny);
            if(res < curValue+tmp[0]){
                res = curValue+tmp[0];
                count = tmp[1];
            }else if(res == curValue+tmp[0])
                count += tmp[1];
        }
        memo.put(x+"/"+y, new int[]{(int)(res%mod), (int)(count%mod)});
        return memo.get(x+"/"+y);
    }
}
