#define pii pair<int, int>
#define mod 1000000007

class Solution {
public:
vector<int> pathsWithMaxScore(vector<string>& ar){
    ar[0][0]='0';
    int n=ar.size();
    pii **dp=new pii*[101];
    for(int i=0;i<=100;i++) dp[i]=new pii[101];
    dp[n-1][n-1]={0,1};             //  {scr,no_of_ways}
    //  initial conditions
    bool gotX=false;
    for(int i=n-2;i>=0;i--){
        gotX=(gotX || (ar[i][n-1]=='X'));
        if(gotX) dp[i][n-1]={-1,0};
        else dp[i][n-1]={(ar[i][n-1]-'0')+dp[i+1][n-1].first,1};
    }
    gotX=false;
    for(int j=n-2;j>=0;j--){
        gotX=(gotX || (ar[n-1][j]=='X'));
        if(gotX) dp[n-1][j]={-1,0};
        else dp[n-1][j]={(ar[n-1][j]-'0')+dp[n-1][j+1].first,1};
