class Solution {
public:
	// first in pair is total sum and second is number of ways
	vector<int> pathsWithMaxScore(vector<string>& board) {
		int n=board.size();
		int m=board[0].size();
		// i have created extra size dp array so the first row and first column is empty 
		vector<vector<pair<int,int>>> dp(n+1,vector<pair<int,int>>(m+1,{0,0}));

		dp[n][m]={0,1}; // the second pair is 1 because it is the base condition as we are starting from last and this is the one path 

		// below code we just make our dp arry in int form
		for(int i=0;i<n;i++){
			for(int j=0;j<m;j++){
				if(board[i][j]=='E' || board[i][j]=='S')
					continue;
				else if(board[i][j]=='X')
					dp[i+1][j+1]={-1,-1};
				else dp[i+1][j+1]={ board[i][j]-'0',0 };
			}
		}

		// this is base condition 
		for(int i=n-1 ; i>0 ; i--){
			if(dp[i][m].first == -1 || dp[i+1][m].first == -1) // we check because we can not go if the below one is x or the one in which we are standing is x 
				continue;
			dp[i][m] = { dp[i][m].first + dp[i+1][m].first , 1 };
		}

		for(int j=m-1 ; j>0 ; j--){
		// we check because we can not go if the below one is x or the one in which we are standing is x 
			if(dp[n][j].first == -1||dp[n][j+1].first==-1)
				continue;
			dp[n][j] = {dp[n][j].first+dp[n][j+1].first,1};
		}


		for(int i=n-1;i>0;i--){
			for(int j=m-1;j>0;j--){

				if(dp[i][j].first == -1 && dp[i][j].second == -1)
					continue;
				// below three conditions are possible  1   1             1   x        1    4
											  //	    1   4             4   5        4    x

				pair<int,int> p = {-1,0};
				if(dp[i+1][j].first == dp[i][j+1].first && dp[i][j+1].first > -1 ) {
					p.first = dp[i+1][j].first;
					p.second = dp[i+1][j].second + dp[i][j+1].second;
				}
				if(p.first < dp[i+1][j].first){
					p.first = dp[i+1][j].first;
					p.second = dp[i+1][j].second;
				}
				if(p.first < dp[i][j+1].first){
					p.first = dp[i][j+1].first;
					p.second=dp[i][j+1].second;
				}

				if(p.first < dp[i+1][j+1].first){
					p.first = dp[i+1][j+1].first;
					p.second=dp[i+1][j+1].second;
				}

				dp[i][j].first= (dp[i][j].first+p.first)%1000000007;
				dp[i][j].second= (dp[i][j].second + p.second)%1000000007;

			}
		}
		vector<int> ans;
		ans.push_back(dp[1][1].first);
		ans.push_back(dp[1][1].second);

		if(ans[1]==0)
			ans[0]=0;

		return ans;
	}
};

