```
class Solution {
public:
    int mod=1000000007;
    //to move left and top and top left cells
    vector<int> offsets={0,-1,-1,0};
    vector<int> pathsWithMaxScore(vector<string>& b) {

        //a backtracking solution with memoization

        //start from the bottom right and for every board[i][j] store the 
        //max path to end and also the number of ways with maximum sum  path

        int n=b.size();

        vector<vector<pair<int,int>>> dp(n,vector<pair<int,int>>(n,{-1,-1}));

         solve(b,n-1,n-1,dp,n);

        return{dp[n-1][n-1].first,dp[n-1][n-1].second};
    }

    pair<int,int> solve(vector<string>& b,int i,int j,vector<vector<pair<int,int>>>& dp,int& n)
    {
        //we have reached the end
        if(b[i][j]=='E')
            return {0,1};

        //if the sub problem is already calculated
        if(dp[i][j].first!=-1)
            return dp[i][j];

        //try 3 possible directions


        long currSum=0;
        int num=0;
        for(int k=0;k<3;k++)
        {
            int x=i+offsets[k];
            int y=j+offsets[k+1];
            if(x<0 || y<0 || x>=n || y>=n || b[x][y]=='X')
                continue;
            auto [returnSum,returnPaths]=solve(b,x,y,dp,n);
            if(returnSum> currSum)
            {
                currSum=returnSum;
                num=returnPaths;
            }
            else if(returnSum==currSum){
                num+=returnPaths;
                num%=mod;
            }
        }
        //no path exists to reach the end from b[i][j]
        if(currSum==0 && num==0)
            return dp[i][j]={0,0};

        //there exists a path
        if(isdigit(b[i][j]))
            currSum+=(b[i][j]-'0');
        currSum%=mod;
        return dp[i][j]={  currSum ,num%(mod)};

    }
};
```
