class Solution {
    public int[] pathsWithMaxScore(List<String> board) {
        int m = board.size(), n = board.get(0).length(); // length and width of board

        int[][] dp = new int[m][n]; // initialize dp array to store maximum sum
        dp[m - 1][n- 1] = 0;

        long[][] count = new long[m][n]; // initialize path array to store maximum path count
        count[m - 1][n- 1] = 1L;

        for(int i = n - 2; i >= 0; i--) { // traverse and fill the bottom array from matrix
            char c = board.get(m - 1).charAt(i);
            if(c == 'X') {
                for(int j = 0; j <= i; j++) { // fill the remaining with -1 when 'X' encountered
                    dp[m - 1][j] = -1;
                }
                break;
            }
            dp[m - 1][i] = dp[m - 1][i + 1] + (c - '0'); // update the dp array as current value + previous value to left
            count[m - 1][i] += count[m - 1][i + 1]; // update the path count array as previous value to left + 1
        }
        for(int i = m - 2; i >= 0; i--) { // traverse and fill the rightmost array from matrix
            char c = board.get(i).charAt(n - 1);
            if(c == 'X') {
                for(int j = 0; j <= i; j++) { // fill the remaining with -1 when 'X' encountered
                    dp[j][n - 1] = -1;
                }
                break;
            }
            dp[i][n - 1] = dp[i + 1][n - 1] + (c - '0'); // update the dp array as current value + previous value at bottom
            count[i][n - 1] += count[i + 1][n - 1]; // update the path count array as previous value at bottom + 1
        }

        for(int i = m - 2; i >= 0; i--) { // traverse the remaning dp array
            for(int j = n - 2; j >= 0; j--) {
                char c = board.get(i).charAt(j);                
                if(c == 'X') { // if 'X' update the dp value to -1 to mark as unreachable
                    dp[i][j] = -1;
                    continue;
                }
                int max = Math.max(dp[i + 1][j + 1], Math.max(dp[i + 1][j], dp[i][j + 1])); //find the max value from the 3 paths avaiable i.e diagonal, right and below
                if(dp[i + 1][j + 1] == max) { //update the path count array if the max value came from diagonal
                    count[i][j] = (count[i][j] + count[i + 1][j + 1]) % 1000000007;
                }
                if(dp[i + 1][j] == max) { //update the path count array if the max value came from below
                    count[i][j] = (count[i][j] + count[i + 1][j]) % 1000000007;
                }
                if(dp[i][j + 1] == max) { //update the path count array if the max value came from right
                    count[i][j] = (count[i][j] + count[i][j + 1]) % 1000000007;
                }
                if(max == -1) { //if blocked then update to -1 or 0 if we reach (0,0)
                    dp[i][j] = (i == 0 && j == 0) ? 0 : -1;
                } else {
                    dp[i][j] = max + ((c == 'E') ? 0 : c - '0'); // else update max + current value 
                }
            }
        }
        return new int[]{dp[0][0], (int) count[0][0]}; // return (0,0) from dp and count path array
    }
