class Solution {
    #define mod 1000000007
public:
    vector<int> pathsWithMaxScore(vector<string>& mat) {
        int n = mat.size();

        int m = mat[0].size();

        int dp[n+1][m+1], count[n+1][m+1];

        memset(dp, 0, sizeof(dp));

        memset(count, 0, sizeof(count));

        dp[0][0] = 0;

        count[0][0] = 1;

        //fill rows first upto obstacle not found 
        for(int i=1;i<n;i++){

            if(mat[0][i]=='X'){

                break;
            }

            dp[0][i] = dp[0][i-1] + (mat[0][i] - '0');

            count[0][i] = 1;
        }
        //fill colums first upto obstacle not found 
        for(int i=1;i<m;i++){

            if(mat[i][0]=='X'){

                break;
            }

            dp[i][0] = dp[i-1][0] + (mat[i][0] - '0');

            count[i][0] = 1;
        }

        for(int i=1;i<n;i++){

            for(int j=1;j<m;j++){

                if(mat[i][j]=='X') {
                    continue;
                }

                int value = mat[i][j]=='S' ? 0 : mat[i][j]-'0';

                int val = max(dp[i-1][j-1], max(dp[i-1][j], dp[i][j-1])) ;

                if(val == 0){

                    continue;
                }

                dp[i][j] = val + value;

                if(val == dp[i-1][j-1]){

                    count[i][j] = (count[i][j] + count[i-1][j-1])%mod;
                } 

                if(val == dp[i-1][j]){

                    count[i][j] = (count[i][j] + count[i-1][j])%mod;
                }

                if(val == dp[i][j-1]){

                    count[i][j] = (count[i][j] + count[i][j-1])%mod;
                } 
            }
        }

        if(n == 2 && m == 2 && dp[n-1][m-1] == 0 && count[n-1][m-1] == 0) {

            return {0,1};
        } 

        return {dp[n-1][m-1],count[n-1][m-1]};
    }
};
