    vector<int> pathsWithMaxScore(vector<string>& board) {
        int n = board.size();
        vector<vector<pair<int,int>>> dp(n,vector<pair<int,int>>(n,{0,0}));
        dp[n-1][n-1] = {0,1};
        if(board[n-2][n-1]!='X')
            dp[n-2][n-1] = {board[n-2][n-1]-'0',1};
        if(board[n-1][n-2]!='X')
            dp[n-1][n-2] = {board[n-1][n-2]-'0',1};
        for(int i=n-3;i>=0;i--)
        {
            if(board[i][n-1]=='X')
                continue;
            if(dp[i+1][n-1].first!=0)
                dp[i][n-1] = {(dp[i+1][n-1].first+board[i][n-1]-'0')%1000000007,1};
        }
        for(int i=n-3;i>=0;i--)
        {
            if(board[n-1][i]=='X')
                continue;
            if(dp[n-1][i+1].first!=0)
                dp[n-1][i] = {(dp[n-1][i+1].first+board[n-1][i]-'0')%1000000007,1};
        }
        for(int i=n-2;i>=0;i--)
        {
            for(int j=n-2;j>=0;j--)
            {
                if(board[i][j]!='X')
                {
                    if(dp[i+1][j].first!=0)
                    {
                        dp[i][j] = dp[i+1][j];
                    }
                    if(dp[i][j+1].first!=0)
                    {
                        if(dp[i][j].first==dp[i][j+1].first)
                        {
                            dp[i][j] = {dp[i][j].first,(dp[i][j].second+dp[i][j+1].second)%1000000007};
                        }
                        else
                        {
                            if(dp[i][j].first<dp[i][j+1].first)
                            {
                                dp[i][j] = dp[i][j+1];
                            }
                        }
                    }
                    if(board[i+1][j+1]=='S' || dp[i+1][j+1].first!=0)
                    {
                        if(dp[i][j].first==dp[i+1][j+1].first)
                        {
                            dp[i][j] = {dp[i][j].first,(dp[i][j].second+dp[i+1][j+1].second)%1000000007};
                        }
                        else
                        {
                            if(dp[i][j].first<dp[i+1][j+1].first)
                            {
                                dp[i][j] = dp[i+1][j+1];
                            }
                        }
                    }
                    if(board[i][j]!='E' && (board[i+1][j+1]=='S' || dp[i][j].first!=0))
                        dp[i][j].first= (dp[i][j].first+(board[i][j]-'0'))%1000000007;
                }
            }
        }
        return {dp[0][0].first%1000000007,dp[0][0].second%1000000007};
    }
};
