class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int n = board.size();
        int m = board[0].size();

        pair<int, int> dp[n][m];
        dp[0][0] = {0, 1};

        //filling up the first row and column as they are the base cases
        int MOD = (int)(1e9 + 7);
        for (int i = 1; i < m; i++) {
            //If current pos is 'X' then give it a very low value so that it never gets picked
            if (board[0][i] == 'X') {
                dp[0][i] = {-MOD, 0};  
                continue;
            }

            dp[0][i].first = (dp[0][i - 1].first + (int)(board[0][i] - '0'));
            dp[0][i].second = 1;

        }

        for (int i = 1; i < n; i++) {
            if (board[i][0] == 'X') {
                dp[i][0] = {-MOD, 0};  
                continue;
            }

            dp[i][0].first = (dp[i - 1][0].first + (int)(board[i][0] - '0'));
            dp[i][0].second = 1;
        }

        for (int i = 1; i < n; i++) {
            for (int j = 1; j < m; j++) {

                if (board[i][j] == 'X') {
                    dp[i][j] = {-MOD, 0};
                    continue;
                }

                //Traverse over the 3 neighbours and find out the max value possible while updating the ways too

                int maxVal = -1; //set maxVal as -1 not 0 as 0 is also a feasable option(eg: for (0,0) neighbours)
                if (dp[i - 1][j - 1].first > maxVal) {
                    maxVal = dp[i - 1][j - 1].first;
                    dp[i][j].second = dp[i - 1][j - 1].second;
                } else if (dp[i - 1][j - 1].first == maxVal) {
                    // if this value is same as max till now we can use it's path as well
                    dp[i][j].second = (dp[i][j].second + dp[i - 1][j - 1].second) % MOD;
                }

                if (dp[i - 1][j].first > maxVal) {
                    maxVal = dp[i - 1][j].first;
                    dp[i][j].second = dp[i - 1][j].second;
                } else if (dp[i - 1][j].first == maxVal) {
                    dp[i][j].second = (dp[i][j].second + dp[i - 1][j].second) % MOD;
                }

                if (dp[i][j - 1].first > maxVal) {
                    maxVal = dp[i][j - 1].first;
                    dp[i][j].second = dp[i][j - 1].second;
                } else if (dp[i][j - 1].first == maxVal) {
                    dp[i][j].second = (dp[i][j].second + dp[i][j - 1].second) % MOD;
                }

                //maxVal < 0 indicates that no neighbour is connected to (0,0) so mark this node with least value
                if (maxVal < 0) {
                     dp[i][j] = {-MOD, 0};
                } else {
                    dp[i][j].first =  maxVal + (int)(board[i][j] - '0');
                }

            }
        }

        vector<int> ans;
        if (dp[n - 1][m - 1].first == -MOD) return {0, 0};

        //subtracting (int)(board[n - 1][m - 1] - '0' as source node has added ('S' - '0') which should have been 0
        return {dp[n - 1][m - 1].first - (int)(board[n - 1][m - 1] - '0'), dp[n - 1][m - 1].second};
    }
};
