class Solution {
    private static final int MOD = 1000000007;
    int[][] dir = {
        {-1, 0},
        {0, -1},
        {-1, -1}
    };

    public int[] pathsWithMaxScore(List<String> board) {
        int rows = board.size();
        int cols = board.get(0).length();
        int[][] memo = new int[rows][cols];
        int[][] ways = new int[rows][cols];
        fill(board, rows, cols, memo, ways);
        int[] result = getMaxNeighborAndWays(memo, ways, rows - 1, cols - 1);
        if (result[0] == -1) {
            result = new int[] {0,0};
        }
        return result;
    }

    private void fill(List<String> board, int rows, int cols, int[][] memo, int[][] ways) {
        for (int row = 0; row < rows; row++) {
            for (int col=0; col<cols; col++) {
                char c = board.get(row).charAt(col);
                if (row==0 && col==0) {
                    ways[row][col] = 1;
                    continue;
                }
                if (isDigit(c)) {
                    int val = c - '0';
                    int[] naw = getMaxNeighborAndWays(memo, ways, row, col);
                    int max = naw[0];

                    if (max != -1) {
                        memo[row][col] = (max + val);
                        ways[row][col] = naw[1];
                    } else {
                        memo[row][col] = -1;
                    }
                } else if (c == 'X') {
                    memo[row][col] = -1;
                }
            }
        }
    }

    private int[] getMaxNeighborAndWays(int[][] memo, int[][] ways, int row, int col) {
        int max = -1;
        int waycount = 0;
        for (int[] d : dir) {
            int nr = row + d[0];
            int nc = col + d[1];
            if (nr >= 0 && nc >= 0 && memo[nr][nc] > -1) {
                int i = memo[nr][nc];
                if (i == max) {
                    waycount+=ways[nr][nc];
                } else if (i > max) {
                    waycount=ways[nr][nc];
                    max = i;
                }
            }
        }
        return new int[] {(int)(max % MOD), ((int)waycount  % MOD)};
    }

    private boolean isDigit(char c) {
        return (c >= '0' && c <= '9');
    }
}
