class Solution {
public:
  vector<int> pathsWithMaxScore(vector<string>& board) {

    int n = board.size(), nend = board.size() - 1;
    vector<vector<int>> mat(n, vector<int>(n));
    vector<vector<int>> level(n, vector<int>(n));
    board[nend][nend] = '0', board[0][0] = '0' , mat[0][0] = 0;

    //construct array of muximum for path
    for(int y = 1; y < n; y++)
      if(board[y][0] != 'X') mat[y][0] = board[y][0] - '0' + mat[y-1][0];
      else mat[y][0] = 0;

    for(int x = 1; x < n; x++) 
      if(board[0][x] != 'X') mat[0][x] = board[0][x] - '0' + mat[0][x-1];
      else mat[0][x] = 0;

    for(int y = 1; y < n; y++)
      for(int x = 1; x < n; x++)
        if(board[y][x] == 'X') mat[y][x] = 0;
        else mat[y][x] = board[y][x] - '0' + max(mat[y-1][x-1],max(mat[y-1][x],mat[y][x-1]));

    //construct array of level of steps for maximum
    for(int y = 0; y < n; y++)
      if(board[y][0] != 'X') level[y][0] = 1;
      else for(; y < n; y++) level[y][0] = 0;

    for(int x = 0; x < n ; x++)
      if(board[0][x] != 'X') level[0][x] = 1;
      else for(; x < n; x++) level[0][x] = 0;

    for(int y = 1; y < n; y++)
			for(int x = 1; x < n; x++){
				if(board[y][x] == 'X') {level[y][x] = 0; continue;}
        int t = board[y][x] - '0';

				if(mat[y-1][x] + t == mat[y][x]) level[y][x] += level[y-1][x];
				if(mat[y][x-1] + t == mat[y][x]) level[y][x] += level[y][x-1];
				if(mat[y-1][x-1] + t == mat[y][x]) level[y][x] += level[y-1][x-1];

				level[y][x] %= 1000000007;
			}

		if(level[nend][nend] == 0) return {0,0};
		return {mat[nend][nend] , level[nend][nend]};
  }
};

