	@functools.lru_cache(None)
	def helper(i, j):

		if i == j == 0:
			return (0, 1) # We reached the end, return 0 points and 1 path

		if (i < 0) or (j < 0):
			return (float('-inf'), 0) # Treat going off the board like a wall, return -inf

		options = [helper(i-1, j), helper(i-1, j-1), helper(i, j-1)]
		target = max(options, key = lambda opt: opt[0])[0]
		return (board[i][j] + target, sum((opt[1] for opt in options if opt[0] == target)))


