class Solution {
    public int[] pathsWithMaxScore(List<String> board) {
        if (board==null || board.size()==0)
            return new int[2];
        final int MOD = 1_000_000_007;
        int len = board.size();
        int[][] sum = new int[len][len];
        int[][] counts = new int[len][len]; 
        counts[len-1][len-1] = 1;
        final int[][] neighs = new int[][]{
            {1,0}, {0,1}, {1,1}
        };
        for(int i=len-1;i>=0;i--) {
            for(int j=len-1;j>=0;j--) {
                int val = getValue(board.get(i).charAt(j));
                if (val==-1)
                    continue;
                // System.out.println("Processing ("+i+","+j+")");
                for(int[] neigh : neighs) {
                    int row = i + neigh[0];
                    int col = j + neigh[1];
                    if (row>len-1 || col>len-1 || counts[row][col]==0)
                        continue;
                    // System.out.println("Processing Neighbor("+row+","+col+")");
                    int newSum = val + sum[row][col];
                    if (newSum<sum[i][j])
                        continue;
                    if (newSum==sum[i][j]) {
                        counts[i][j] = (counts[i][j] + counts[row][col])%MOD;
                    }
                    if (newSum>sum[i][j]) {
                        sum[i][j]=newSum;
                        counts[i][j] = counts[row][col];
                    }
                }
            }
        }
        return new int[]{sum[0][0], counts[0][0]};
    }

    private int getValue(char c) {
        if (c=='S' || c=='E') {
            return 0;
        }
        if (c=='X')
            return -1;
        return c-'0';
    }
}
