import org.junit.Test;

import java.util.Arrays;
import java.util.List;
import java.util.Optional;

import static org.junit.Assert.assertArrayEquals;

public class Solution {

    private static class Node {

        final int val;
        int sum = -1;
        int numberOfWays = 1;

        Node(final int val) {
            this.val = val;
        }

    }

    private static final Node X = new Node(-1);

    public int[] pathsWithMaxScore(final List<String> board) {
        final int n = board.size();
        final Node[][] dp = new Node[n][n];

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                final int x = n - 1 - i;
                final int y = n - 1 - j;
                final char c = board.get(x).charAt(y);
                int val = c - '0';

                if (c == 'S' || c == 'E') {
                    val = 0;
                } else if (c == 'X') {
                    val = -1;
                }

                dp[i][j] = new Node(val);

                if (val == -1) {
                    continue;
                }

                if (i == 0 && j == 0) {
                    dp[0][0].sum = 0;
                    continue;
                }

                Node top = X;

                if (i >= 1) {
                    top = dp[i - 1][j];
                }

                Node left = X;

                if (j >= 1) {
                    left = dp[i][j - 1];
                }

                Node topLeft = X;

                if (i >= 1 && j >= 1) {
                    topLeft = dp[i - 1][j - 1];
                }

                final int maxSum = Math.max(Math.max(top.sum, left.sum), topLeft.sum);

                if (maxSum != -1) {
                    long numberOfWays = 0L;

                    if (top.sum == maxSum) {
                        numberOfWays = numberOfWays + top.numberOfWays;
                    }

                    if (left.sum == maxSum) {
                        numberOfWays = numberOfWays + left.numberOfWays;
                    }

                    if (topLeft.sum == maxSum) {
                        numberOfWays = numberOfWays + topLeft.numberOfWays;
                    }

                    dp[i][j].sum = dp[i][j].val + maxSum;
                    dp[i][j].numberOfWays = (int) (numberOfWays % (1e9 + 7));
                }
            }
        }

        final Node end = dp[n - 1][n - 1];

        if (end.sum == -1) {
            return new int[]{0, 0};
        }

        return new int[]{end.sum, end.numberOfWays};
    }

    @Test
    public void test() {
