                    long numberOfWays = 0L;

                    if (top.sum == maxSum) {
                        numberOfWays = numberOfWays + top.numberOfWays;
                    }

                    if (left.sum == maxSum) {
                        numberOfWays = numberOfWays + left.numberOfWays;
                    }

                    if (topLeft.sum == maxSum) {
                        numberOfWays = numberOfWays + topLeft.numberOfWays;
                    }

                    dp[i][j].sum = dp[i][j].val + maxSum;
                    dp[i][j].numberOfWays = (int) (numberOfWays % (1e9 + 7));
                }
            }
        }

        final Node end = dp[n - 1][n - 1];

        if (end.sum == -1) {
            return new int[]{0, 0};
        }

        return new int[]{end.sum, end.numberOfWays};
    }

    @Test
    public void test() {
        assertArrayEquals(new int[]{34, 3}, new Solution().pathsWithMaxScore(Arrays.asList("E11345","X452XX","3X43X4","422812","284522","13422S")));
        assertArrayEquals(new int[]{7, 1}, new Solution().pathsWithMaxScore(Arrays.asList("E23","2X2","12S")));
        assertArrayEquals(new int[]{4, 2}, new Solution().pathsWithMaxScore(Arrays.asList("E12","1X1","21S")));
        assertArrayEquals(new int[]{0, 0}, new Solution().pathsWithMaxScore(Arrays.asList("E11","XXX","11S")));
    }

}
