    long mod=(long)(1e9+7);
    int n,m;

    class data
    {
        long val=0;
        long path=0;

        data(long val,long path)
        {
            this.val=val;
            this.path=path;
        }
    }
    public int[] pathsWithMaxScore(List<String> board) 
    {
        n=board.size();
        m=board.get(0).length();

        data dp[][]=new data[n][m];

        data ans=helper(board,dp,0,0);

        int val=ans.val<0?0:(int)ans.val;
        int path=ans.val<0?0:(int)ans.path;
        return new int[]{val,path};
    }

    private data helper(List<String>board,data dp[][],int start,int end)
    {
        if(start==n-1&&end==m-1)
            return new data(0,1);

        if(start>=n||end>=m||board.get(start).charAt(end)=='X')
            return new data(Integer.MIN_VALUE,0);

        if(dp[start][end]!=null)
            return dp[start][end];

        data val1=helper(board,dp,start+1,end);
        data val2=helper(board,dp,start+1,end+1);
        data val3=helper(board,dp,start,end+1);

        long val=Math.max(val1.val,Math.max(val2.val,val3.val));

        int path=0;
        if(val>=0)
        {
            if(val==val1.val)
                path+=val1.path;

            if(val==val2.val)
                path+=val2.path;

            if(val==val3.val)
                path+=val3.path;
        }

        if(start>0||end>0)
            val+=(board.get(start).charAt(end)-'0');

        val%=mod;
        path%=mod;

        dp[start][end]=new data(val,path);
        return dp[start][end];
    }
