class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int n = board.size(), m = board[0].size(), j, mod = 1e9 + 7;
        vector<vector<int>> val(n, vector<int>(m)), times(n, vector<int>(m)), dirs = {{0,-1},{-1,0},{-1,-1}};
        times[n-1][m-1] = 1;
        for (int sum = n + m - 2; sum; sum--)
            for (int i = n-1; i >= 0; i--)
            {
                if (sum < i || sum - i >= m) continue;
                j = sum - i;
                if (board[i][j] == 'X' || !times[i][j]) continue;
                for (vector<int>& d : dirs)
                {
                    int tmpi = i + d[0], tmpj = j + d[1];
                    if (tmpi < 0 || tmpj < 0 || board[tmpi][tmpj] == 'X') continue;
                    int currval = val[i][j] + (board[tmpi][tmpj] == 'E' ? 0 : (board[tmpi][tmpj] - '0'));
                    if (currval == val[tmpi][tmpj]) times[tmpi][tmpj] = (times[tmpi][tmpj] + times[i][j]) % mod;
                    else if (currval > val[tmpi][tmpj])
                    {
                        val[tmpi][tmpj] = currval;
                        times[tmpi][tmpj] = times[i][j];
                    }
                }
            }
        return {val[0][0], times[0][0]};
    }
};
