class Solution:
    def pathsWithMaxScore(self, board: List[str]) -> List[int]:
        mod=10**9+7
        dp1=[[0]*len(board[0]) for i in range(len(board))]
        dp2=[[0]*len(board[0]) for i in range(len(board))]
        for i in range(len(board)):
            for j in range(len(board[0])):
                if board[i][j]=='E':
                    dp1[i][j]=0
                    dp2[i][j]=1
                    continue
                if board[i][j]!='X':
                    s1=dp1[i-1][j] if i-1>=0 and dp1[i-1][j]>=0 else -1
                    s2=dp1[i][j-1] if j-1>=0 and dp1[i][j-1]>=0 else -1
                    s3=dp1[i-1][j-1] if i-1>=0 and j-1>=0 and dp1[i-1][j-1]>=0 else -1
                    if s1==-1 and s2==-1 and s3==-1:
                        dp1[i][j]=-1
                        dp2[i][j]=0
                    else:
                        dp1[i][j]=int(board[i][j]) if board[i][j]!='S' else 0
                        s=max(s1,s2,s3)
                        arr=[dp2[i-1][j],dp2[i][j-1],dp2[i-1][j-1]]
                        dp1[i][j]=(s+dp1[i][j])%mod

                        for k,v in enumerate([s1,s2,s3]):
                            if v==s:
                                dp2[i][j]=(dp2[i][j]+arr[k])%mod
                else:
                    dp1[i][j]=-1
                    dp2[i][j]=0
        return [dp1[-1][-1] if dp1[-1][-1]>-1 else 0,dp2[-1][-1]]
