#define mod 1000000007

class Solution {
public:
    vector<int> pathsWithMaxScore(vector<string>& board) {
        int n = board.size();
        vector<vector<pair<int, int>>> dp(n + 1, vector<pair<int, int>>(n+1, {0, 1}));

        for (int i = 0; i <= n; i++)
            dp[i][n].first = dp[n][i].first = -1;

        for (int i = n-1; i >= 0; i--) {
            for (int j = n-1; j >= 0; j--) {
                char digit = board[i][j];
                dp[i][j] = calculate(dp, digit, i, j);
            }
        }

        if (dp[0][0].first < 0)
            return {0, 0};

        return { dp[0][0].first, dp[0][0].second };
    }

private:
    pair<int, int> calculate(const vector<vector<pair<int, int>>> &dp, char digit, int y, int x) {
        int value;
        if (digit == 'E') {
            value = 0;
        } else if (digit >= '0' && digit <= '9') {
            value = digit - '0';
        } else {
            return { digit == 'S' ? 0 : -1, 1 };
        }

        pair<int, int> current = {-1, 0};
        for (int i = 0; i <= 1; i++) {
            for (int j = 0; j <= 1; j++) {
                int total = dp[y+i][x+j].first;
                int counter = dp[y+i][x+j].second;

                if (total < 0)
                    continue;

                if (i == 0 && j == 0)
                    continue;

                if (total > current.first)
                    current = { total, 0 };

                if (total == current.first)
                    current.second = (current.second + counter) % mod;
            }
        }

        if (current.first < 0)
            return current;

        current.first += value;
        return current;
    }
};
