        int n = board.size();
        vector<int> ans;
        int mod = pow(10, 9) + 7;
        vector<vector<pair<unsigned int, unsigned int>>> dp(n, vector<pair<unsigned int, unsigned int>>(n, make_pair(0, 0)));
        for (int j=1; j<n; j++) {
            if (board[0][j] == 'X') break;
            dp[0][j] = make_pair(dp[0][j-1].first + board[0][j] - '0', 1);
        }
        for (int i=1; i<n; i++) {
            if (board[i][0] == 'X') break;
            dp[i][0] = make_pair(dp[i-1][0].first + board[i][0] - '0', 1);
        }
        for (int i=1; i<n; i++) {
            for (int j=1; j<n; j++) {
                if (board[i][j] == 'X') continue;
                unsigned int score = max(dp[i-1][j].first, max(dp[i-1][j-1].first, dp[i][j-1].first));
                if (score == 0) {
                    if (board[i-1][j-1] == 'E' && board[i][j] == 'S')
                        dp[i][j] = make_pair(0, 1);
                    else 
                        continue;
                }
                else {
                    unsigned int paths = 0;
                    if (score == dp[i-1][j].first) paths += dp[i-1][j].second;
                    if (score == dp[i-1][j-1].first) paths += dp[i-1][j-1].second;
                    if (score == dp[i][j-1].first) paths += dp[i][j-1].second;
                    score %= mod;
                    if (board[i][j] == 'S')
                        dp[i][j] = make_pair(score, paths%mod);
                    else
                        dp[i][j] = make_pair(score + board[i][j] - '0', paths%mod);
