class Solution {
    public int totalMoney(int n) {
        int whole = n / 7, part = n % 7, sum = 0;
        for (int i = 1; i <= whole; i++) {
            //it is arithmetic progression for each 7 numbers
            //but already divided by two
            //initial form was: (i + i + 6) * 7 / 2
            sum += (i + 3) * 7;
        }
        sum += ((whole + 1 + whole + part) * part) / 2;
        return sum;
    }
}
