    static void fillMap() {
        int count = 0, sum = 0, increment = 1;
        for (int i = 1; i <= 1000 ; i++) {
            if (count % 7 == 0) {
                increment = 1 + (count / 7);
            }
            sum += increment;
            map.put(i, sum);
            count++;
            increment++;
        }
    }
}
