var totalMoney = function(n) {
    /*
    Exploring the numbers for insights
	Shows the weekly amounts, the weekly totals, the running total, and the related triangular number
    01 02 03 04 05 06 07 = 28 = 7*4 | 7*4  | 10 tri(4)
    02 03 04 05 06 07 08 = 35 = 7*5 | 7*9  | 15 tri(5)
    03 04 05 06 07 08 09 = 42 = 7*6 | 7*15 | 21 tri(6)
    04 05 06 07 08 09 10 = 49 = 7*7 | 7*22 | 28 tri(7)
    05 06 07 08 09 10 11 = 56 = 7*8 | 7*30 | 36 tri(8)
    06 07 08 09 10 11 12 = 63 = 7*9 | 7*39 | 45 tri(9)
    07 08 09 10 11 12 13 = 70 = 7*10| 7*49 | 55 tri(10)
    */
    //Original Math Approach
    /*
    const tri = n => (n * (n + 1)) / 2; //Gets triangular number n
    const wksTot = n => 7 * (tri(n + 3) - 6); //Gets total at end of week n note:6 is tri(3)
    const wks = ~~(n / 7); //Total number of complete weeks
    const days = n % 7; //Total number of days in final partial week
    return wksTot(wks) + (days * wks) + tri(days);
    */

    //Mathematical approach, with conditionals to optimize
    let wksTot = 0; //Default the total across all weeks to 0
    let daysTot = 0; //Default the total across the partial week at the end to 0
    let wks = ~~(n / 7); //shorthand to truncate number of full weeks
    let days = n % 7; //Get remaining number of days
    const tri = n => (n * (n + 1)) / 2; //Helper function to get triangular number n 1,3,6 etc.
    if(days > 0) { //If there is a partial week at the end, calculate daysTot
        daysTot = (days * wks) + tri(days);
    }
    if(n >= 7) { //If there is at least one complete week, calculate wksTot
        wksTot = 7 * (tri(wks + 3) - 6); //Gets total at end of week n note:6 is tri(3)   
    }
    return wksTot + daysTot;
};
```

