class Solution { 
    public int totalMoney(int n) {
        int ans = 0;
        for (int i = 0; i < n; i += 7) { //count full 7 days (weeks)
            ans += 28 + (i / 7) * 7;
        }
        int restDay = 7 - n % 7;   //Rest days, Ex: n = 18, we count 21 days minus 3 days
        int num = 0;
        for (int i = 0; i < restDay; i++) {  //
            num += i;
        }
        int over = (7 + (n / 7)) * restDay - num; // count rest days 
        //Ex: n=18, the over 3 days：9+9+9-0-1-2 = 9+8+7
        return n % 7 == 0 ? ans : ans - over;
    }
}

