class Solution {
public:
    // all formulas are based on sum of arithmetic progression
    int totalMoney(int n) {
        int s = 0;
        auto const k = n / 7; // number of full weeks
        if (k > 0)
            s += 28 * k + 7 * (k - 1) * k / 2;
        auto const i = n % 7; // the remainder of the last partial week 
        return s + k * i + (i + 1) * i / 2;
    }
};
```

