class Solution {
    private static final int DAYS_PER_WEEK = 7;
    private static final int SUM_OF_FIRST_WEEK = 28;

    public int totalMoney(int n) {
        var fullWeekCount = n / DAYS_PER_WEEK;
        var lastMondayN = fullWeekCount * DAYS_PER_WEEK + 1;
        var lastMondayVal = fullWeekCount + 1;
        var lastWeekDaysCount = n - lastMondayN + 1;

        return ariphmeticProgressionSum(SUM_OF_FIRST_WEEK, fullWeekCount, DAYS_PER_WEEK)
            + ariphmeticProgressionSum(lastMondayVal, lastWeekDaysCount, 1);
    }

    private static int ariphmeticProgressionSum(int firstVal, int numOfValues, int diff) {
        //https://en.wikipedia.org/wiki/Arithmetic_progression
        return (2 * firstVal + (numOfValues - 1) * diff) * numOfValues / 2; 
    }
}
```

